function [var_test0_1]=var_threshold(acc,v_threshold)
%% 方差阈值
Var1=[];
window=3;
for i=1:length(acc)-window
    V=var(acc(i:i+window,:));                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
    Var1=[Var1;V];  
end
%方差阈值识别结果
var_test0_1=zeros(length(Var1),1);
for i=1:length(Var1)
    if Var1(i)<=v_threshold
      var_test0_1(i)=1;
    end
end